import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = ({ handleClick, text }) => {
  return (
    <>
      <button onClick={handleClick}>{text}</button>
    </>
  )
}

const Display = ({ number, text }) => {
  return (
    <div>
      <span>{text}</span>
      <span> </span>
      <span>{number}</span>
    </div>

  )
}

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [total, setTotal] = useState([])


  const handleClick = (value, runLogic) => {
    //Logic for changing state
    const newValue = value + 1
    runLogic(newValue)
    setTotal(total.concat(newValue))
    console.log(total);
  }

  const getTotal = () => {
    // console.log("done", total);
    // const newTotal = total.reduce((accumulator, currentValue) => accumulator + currentValue, 0)
    // console.log(newTotal);
    // return total.length === 0 ? 0 : newTotal
  }

  return (
    <div>
      <div>
        <h1>give feedback</h1>
        <Button handleClick={() => handleClick(good, setGood)} text="good" />
        <Button handleClick={() => handleClick(neutral, setNeutral)} text="neutral" />
        <Button handleClick={() => handleClick(bad, setBad)} text="bad" />
      </div>

      <div>
        <h1>statistics</h1>
        <Display text="good" number={good} />
        <Display text="neutral" number={neutral} />
        <Display text="bad" number={bad} />
        {/* <Display text="all" number={all} />
        <Display text="average" number={average} />
        <Display text="positive" number={positive} /> */}
      </div>
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
